// For Add Item to Cart
import { toast } from "react-hot-toast";
import axios from "axios";
import { addCart } from "../redux/actions/cartActions"; // Import Redux action

export const addProduct = (product) => async (dispatch, getState) => {
    try {
        const userEmail = getState().auth.user.email; // Get user email from Redux store

        const response = await axios.post("http://localhost:8080/cart/add", null, {
            params: {
                userEmail,
                productId: product.id,
                quantity: 1,
            },
        });

        toast.success(response.data);

        dispatch(addCart(product)); // Dispatch ADDITEM action to Redux store
    } catch (error) {
        console.error("Error:", error.response ? error.response.data : error.message);
        toast.error("Failed to add product to cart.");
    }
};


// For Delete Item to Cart
export const delCart = (product) =>{
    return {
        type:"DELITEM",
        payload:product
    }
}
