import { useState, useEffect } from "react";
import { Footer, Navbar } from "../components";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import axios from "axios";

const Checkout = () => {
  const state = useSelector((state) => state.handleCart);
  const [step, setStep] = useState(1); // Track the current step (1: Address, 2: Order Summary, 3: Payment)
  const [address, setAddress] = useState({ name: "", email: "", phone: "", address: "" });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [paymentSuccess, setPaymentSuccess] = useState(false);
  const [scriptLoaded, setScriptLoaded] = useState(false);

  useEffect(() => {
    const loadRazorpayScript = () => {
      const script = document.createElement("script");
      script.src = "https://checkout.razorpay.com/v1/checkout.js";
      script.async = true;
      script.onload = () => setScriptLoaded(true);
      script.onerror = () => setError("Failed to load payment gateway.");
      document.body.appendChild(script);
    };
    if (!window.Razorpay) loadRazorpayScript();
    else setScriptLoaded(true);
  }, []);

  let subtotal = state.reduce((acc, item) => acc + item.price * item.qty, 0);
  const shipping = 30.0;
  let totalItems = state.reduce((acc, item) => acc + item.qty, 0);
  const totalAmount = Math.round(subtotal + shipping);

  // Handle Address Form Submission
  const handleAddressSubmit = (e) => {
    e.preventDefault();
    if (!address.name || !address.email || !address.phone || !address.address) {
      alert("Please fill all fields.");
      return;
    }
    setStep(2); // Move to Order Summary
  };

  // Handle Payment
  const handlePayment = async () => {
    if (!scriptLoaded) {
      setError("Payment gateway is still loading.");
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const response = await axios.post("http://localhost:8080/api/create-razorpay-order", {
        amount: totalAmount * 100, // Razorpay requires amount in paise
        currency: "INR",
      });

      const { id, amount, currency } = response.data;
      const options = {
        key: process.env.NEXT_PUBLIC_RAZORPAY_KEY_ID || "rzp_test_YOUR_TEST_KEY",
        amount,
        currency,
        name: "Your Store Name",
        description: "Order Payment",
        order_id: id,
        handler: async (response) => {
          setPaymentSuccess(true);
          alert("Payment successful!");
        },
        prefill: { name: address.name, email: address.email, contact: address.phone },
        theme: { color: "#F37254" },
      };

      const razorpay = new window.Razorpay(options);
      razorpay.open();
    } catch (error) {
      setError("Failed to initialize payment.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Navbar />
      <div className="container my-3 py-3">
        <h1 className="text-center">Checkout</h1>
        <hr />

        {/* Step Navigation */}
        <div className="flex justify-center space-x-4 mb-4">
          <button className={`px-4 py-2 rounded ${step === 1 ? "bg-blue-500 text-white" : "bg-gray-200"}`} onClick={() => setStep(1)}>Address</button>
          <button className={`px-4 py-2 rounded ${step === 2 ? "bg-blue-500 text-white" : "bg-gray-200"}`} onClick={() => setStep(2)} disabled={!address.name}>Order Summary</button>
          <button className={`px-4 py-2 rounded ${step === 3 ? "bg-blue-500 text-white" : "bg-gray-200"}`} onClick={() => setStep(3)} disabled={step < 2}>Payment</button>
        </div>

        {step === 1 && (
          <div className="p-5 border rounded">
            <h2 className="text-lg font-bold">Billing Address</h2>
            <form onSubmit={handleAddressSubmit}>
              <input type="text" placeholder="Full Name" className="w-full p-2 my-2 border rounded" value={address.name} onChange={(e) => setAddress({ ...address, name: e.target.value })} required />
              <input type="email" placeholder="Email" className="w-full p-2 my-2 border rounded" value={address.email} onChange={(e) => setAddress({ ...address, email: e.target.value })} required />
              <input type="text" placeholder="Phone Number" className="w-full p-2 my-2 border rounded" value={address.phone} onChange={(e) => setAddress({ ...address, phone: e.target.value })} required />
              <textarea placeholder="Address" className="w-full p-2 my-2 border rounded" value={address.address} onChange={(e) => setAddress({ ...address, address: e.target.value })} required />
              <button type="submit" className="px-4 py-2 bg-blue-500 text-white rounded">Next: Order Summary</button>
            </form>
          </div>
        )}

        {step === 2 && (
          <div className="p-5 border rounded">
            <h2 className="text-lg font-bold">Order Summary</h2>
            <ul>
              {state.map((item) => (
                <li key={item.id} className="flex justify-between py-2">
                  <span>{item.name} x {item.qty}</span>
                  <span>₹{item.price * item.qty}</span>
                </li>
              ))}
            </ul>
            <hr />
            <p className="font-bold">Subtotal: ₹{Math.round(subtotal)}</p>
            <p className="font-bold">Shipping: ₹{shipping}</p>
            <p className="text-xl font-bold">Total: ₹{totalAmount}</p>
            <button className="px-4 py-2 bg-blue-500 text-white rounded" onClick={() => setStep(3)}>Next: Payment</button>
          </div>
        )}

        {step === 3 && (
          <div className="p-5 border rounded">
            <h2 className="text-lg font-bold">Payment</h2>
            {paymentSuccess ? (
              <p className="text-green-500 font-bold">Payment Successful! 🎉</p>
            ) : (
              <button className="px-4 py-2 bg-green-500 text-white rounded" onClick={handlePayment} disabled={loading}>
                {loading ? "Processing..." : "Pay ₹" + totalAmount}
              </button>
            )}
          </div>
        )}
      </div>
      <Footer />
    </>
  );
};

export default Checkout;
